mapper.select:
select id,dataPath,createTime from tile_infos

    storagePath in
    
        #{imagePath}

select createTime
from tile_infos
where visualPath = #{imagePath}

select imagesetid
from tile_infos
where id = #{tileId}

select storagePath,id,dataPath,createTime
from tile_infos

    
        and imagesetid = #{tileRequestDTO.imageDatasetId}
    
    
        
            and createTime BETWEEN #{tileRequestDTO.beginTime} AND #{tileRequestDTO.endTime}

SELECT DATE_FORMAT(createTime,#{timeStep}) as step, count(*) as count , imagesetid as imageSetId FROM tile_infos

    
        imagesetid in
        
            #{imageSetId}
        
    
    and createTime BETWEEN #{beginTime} AND #{endTime}

group by imageSetId,step order by imageSetId ,step
